import React, { useState } from 'react'
import './Offers.css'
import exclusive_image from '../Assets/exclusive_image.png'

const Offers = () => {
  const [offers, setOffers] = useState([]);
  const [showOffers, setShowOffers] = useState(false);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleCheckNow = async () => {
    setLoading(true);
    setError('');
    try {
      // Simulate fetching offers
      const fetchedOffers = [
        { id: 1, name: 'Womens', discount: '20%', image: 'data:image/jpeg;base64,'},
        { id: 2, name: 'Mens', discount: '15%', image: 'data:image/jpeg;base64,' },
        { id: 3, name: 'Kids', discount: '10%', image: 'data:image/jpeg;base64,' }
      ];
      // Simulate a delay
      setTimeout(() => {
        setOffers(fetchedOffers);
        setShowOffers(true);
        setLoading(false);
      }, 1000);
    } catch (error) {
      setError('Failed to fetch offers');
      setLoading(false);
    }
  }

  return (
    <div className='offers'>
      <div className="offers-left">
          <h1>Exclusive</h1>
          <h1>Offers for You</h1>
          <p>ONLY ON BEST SELLERS PRODUCTS</p>
          <button onClick={handleCheckNow}>Check Now</button>
      </div>
      <div className="offers-right">
        <img src={exclusive_image} alt="Exclusive Offers" />
      </div>
      {loading && <p>Loading offers...</p>}
      {error && <p>{error}</p>}
      {showOffers && !loading && !error && (
        <div className="offers-list">
          <h2>Available Offers:</h2>
          <ul>
            {offers.map(offer => (
              <li key={offer.id}>
                <div className="offer-details">
                  <img src={offer.image} alt={offer.name} />
                  <span>{offer.name}</span>
                </div>
                <span className="offer-discount">{offer.discount} off</span>
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  )
}

export default Offers
